#créer une méthode de tri des handle par taille
def trier(liste_handle)
  comparer_handle = liste_handle.sort_by {|handle| handle.length}
  puts "liste par ordre croissant en termes de nombre de caractère : #{comparer_handle} "
  puts "le plus petit handle étant : #{comparer_handle[0]} " #existe plus simple #{tableau.min_by(&:length)}
end

